import React from 'react';
import './Footer.css';

const Footer = () => {
  return (
    <footer>
      <div className="footer-container">
        <div className="left-col">
          <img src="https://www.gregoriofumagalli.com/images/logo.png" alt="Logo" />
          <div className="social-media">
            <a href="https://twitter.com"><i className="fab fa-twitter"></i></a>
            <a href="https://www.linkedin.com/"><i className="fab fa-linkedin-in"></i></a>
            <a href="https://github.com/"><i className="fab fa-github"></i></a>
          </div>
          <p className="rights-text">Â© 2023 Gregorio Fumagalli</p>
        </div>

        <div className="right-col">
          <h1>Subscribe to our Newsletter</h1>
          <div className="border"></div>
          <p>Enter your email to receive our newsletter</p>
          <form action="" className="newsletter-form">
            <input type="text" className="txtb" placeholder="Enter Your Email" />
            <input type="submit" className="btn" value="submit" />
          </form>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
