import React from 'react';
import { useParams } from 'react-router-dom';

const ProjectDetail = () => {
  const { id } = useParams();

  // fetch project details from API or database using the id parameter

  const projects = [
    {
      id: 1,
      title: 'PlanPerfect',
      description: 'A python based Event and Task Management app',
      githubLink: 'https://github.com/warbyd/Python-Project-Events-Planner'
    },
    {
      id: 2,
      title: 'LilyPad',
      description: 'A social media app created for Frogs!',
      githubLink: 'https://github.com/efgeri/LilyPad'
    }
  ];

  const project = projects.find(p => p.id === parseInt(id));

  return (
    <div>
      <h2>{project.title}</h2>
      <p>{project.description}</p>
      <p>GitHub Link: <a href={project.githubLink}>{project.githubLink}</a></p>
    </div>
  );
};

export default ProjectDetail;
