import React from 'react';
import Projects from './Projects';

const ProjectDetail = () => {
  const { id } = useParams();

  // find the project based on the id parameter from the URL
  const project = Projects.find(project => project.id === Number(id));

  // check the project title and set the GitHub link accordingly
  let gitHubLink = '';
  if (project.title === 'Python Event Planner') {
    gitHubLink = 'https://github.com/warbyd/Python-Project-Events-Planner';
  } else if (project.title === 'LilyPad') {
    gitHubLink = 'https://github.com/efgeri/LilyPad';
  }

  return (
    <div>
      <h2>{project.title}</h2>
      <p>{project.description}</p>
      {gitHubLink && <p>GitHub Link: <a href={gitHubLink}>{gitHubLink}</a></p>}
    </div>
  );
};

export default ProjectDetail;
