import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const FooterContainer = styled.footer`
  background-color: #333;
  color: #fff;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  min-height: 100vh;
`;

const HireContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
`;

const HireButtons = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  margin-top: 1rem;
`;

const HireButton = styled.a`
  display: inline-block;
  margin-right: 1rem;
  padding: 0.5rem 1rem;
  font-size: 1rem;
  font-family: 'Raleway';
  color: #fff;
  background-color: #000;
  text-decoration: none;
  border-radius: 5px;
  transition: all 0.3s ease;
  
  &:hover {
    background-color: #fff;
    color: #000;
  }
`;

const NavigationContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const NavLinks = styled.ul`
  margin: 0;
  padding: 0;
  list-style: none;
`;

const NavLink = styled(Link)`
  color: #fff;
  text-decoration: none;
  font-family: 'Raleway';
  font-size: 1.2rem;
  margin-bottom: 0.5rem;
  
  &:hover {
    color: #ccc;
  }
`;

const BuiltBy = styled.p`
  margin-top: 1rem;
`;

const Footer = () => {
  return (
    <FooterContainer>
      <HireContainer>
        <h2>Hire me!</h2>
        <p>Like what you see?<br /><br />Arrange an informal chat via Email or<br />LinkedIn to see if Iâ€™m a good fit!</p>
        <HireButtons>
          <HireButton href="mailto:email@example.com">Email</HireButton>
          <HireButton href="https://www.linkedin.com/in/your-linkedin-profile/" target="_blank" rel="noopener noreferrer">LinkedIn</HireButton>
        </HireButtons>
      </HireContainer>
      <NavigationContainer>
        <h2>Navigation</h2>
        <NavLinks>
          <li><NavLink to="/portfolio">Home</NavLink></li>
          <li><NavLink to="/cv">View my CV</NavLink></li>
          <li><NavLink to="/about">About me</NavLink></li>
          <li><NavLink to="/contact">Contact me</NavLink></li>
        </NavLinks>
      </NavigationContainer>
      <BuiltBy>&copy; 2023 built by Dale Warburton</BuiltBy>
    </FooterContainer>
  );
}

export default Footer;
